import React, { useState } from 'react';
import { Box, Container, Text, Input, IconButton } from '@chakra-ui/react';
import { FaPaperPlane } from "react-icons/fa";
import axios from 'axios';

const ChatMain = ({ chatrooms }) => {
  const [activeChatroom, setActiveChatroom] = useState(null);
  const [question, setQuestion] = useState('');
  const [messages, setMessages] = useState([]);

  const handleChange = (e) => {
    setQuestion(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:5000/query', { question });
      const formattedAnswer = response.data.answer.replace(/\n/g, '<br>');
      setMessages([...messages, { question, answer: formattedAnswer }]);
      setQuestion('');
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <Box>
      <Container bgColor="#646464" maxW="full" h="50px" alignItems="center" justifyItems="center" display="flex">
        <Text fontSize="18px" color="white" margin="auto" as='b'>
          New chat room
        </Text>
      </Container>
      <Box className="chat-container" paddingTop="20px" height="auto" maxHeight="400px" overflowY="scroll">
        {activeChatroom && messages.filter(message => message.chatroomId === activeChatroom.id).map((message, index) => (
          <Box key={index} className="chat-message"  display="flex" flexDirection="column" marginBottom="2rem" fontSize="16px">
            <Box className="question" color="white" bgColor="#0066CC" textAlign="left" padding="10px" borderRadius="5px" marginLeft="auto" marginBottom="5px" maxW="50%">
              {message.question}
            </Box>
            <Box className="answer" color="white" bgColor="#373737" textAlign="left" padding="10px" borderRadius="5px" marginRight="auto" marginTop="5px" maxW="50%">
              <div dangerouslySetInnerHTML={{ __html: message.answer }} />
            </Box>
          </Box>
        ))}
      </Box>
      <Box as="form" onSubmit={handleSubmit}>
        <Input type="text" className="chat-input" value={question} onChange={handleChange} width="calc(100% - 80px)" padding="10px" marginBottom="10px" border="1px solid #ccc" borderRadius="5px" fontSize="16px"/>
        <IconButton
          type="submit"
          className="chat-button"
          aria-label="Send message"
          icon={<FaPaperPlane />}
          width="80px"
          padding="10px"
          color="white"
          bgColor="#4CAF50"
          border="none"
          borderRadius="5px"
          cursor="pointer"
          fontSize="16px"
        />
      </Box>
    </Box>
  );
};

export default ChatMain;
