import React, { useState } from 'react';
import { Button, FormControl, FormLabel, Input, Box, Center, HStack, CheckboxGroup, Checkbox } from '@chakra-ui/react';
import axios from 'axios';

const Register = () => {
  const [formData, setFormData] = useState({
    fname: '',
    lname: '',
    tel_no: '',
    email: '',
    dob: '',
    address: '',
    gender: '',
    username: '',
    password: '',
    name: '',
  });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    // For the gender field, parse the value to an integer
    const parsedValue = name === 'gender' ? parseInt(value, 10) : value;
    setFormData({ ...formData, [name]: parsedValue });
  };
  

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(formData);
    try {
      const response = await axios.post('/api/signup', formData);
      console.log(response.data);
      // Redirect or perform actions upon successful registration
    } catch (error) {
      setError(error.response.data.error);
    }
  };

  return (
    <Center h="100vh">
      <Box p={8} borderWidth={1} borderRadius={8} boxShadow="lg">
        <form onSubmit={handleSubmit}>
          <FormControl id="fname">
            <FormLabel>First Name</FormLabel>
            <Input
              type="text"
              name="fname"
              value={formData.fname}
              onChange={handleChange}
            />
          </FormControl>
          <FormControl mt={4} id="lname">
            <FormLabel>Last Name</FormLabel>
            <Input
              type="text"
              name="lname"
              value={formData.lname}
              onChange={handleChange}
            />
          </FormControl>
          <FormControl mt={4} id="tel_no">
            <FormLabel>Telephone Number</FormLabel>
            <Input
              type="tel"
              name="tel_no"
              value={formData.tel_no}
              onChange={handleChange}
            />
          </FormControl>
          <FormControl mt={4} id="email">
            <FormLabel>Email</FormLabel>
            <Input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
            />
          </FormControl>
          <FormControl mt={4} id="dob">
            <FormLabel>Date of Birth</FormLabel>
            <Input
              type="date"
              name="dob"
              value={formData.dob}
              onChange={handleChange}
            />
          </FormControl>
          <FormControl mt={4} id="address">
            <FormLabel>Address</FormLabel>
            <Input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleChange}
            />
          </FormControl>
          <FormControl id="gender">
              <FormLabel>Gender</FormLabel>
              <CheckboxGroup colorScheme="blue" value={formData.gender} onChange={handleChange}>
                <HStack spacing={4}>
                  <Checkbox value="1">Male</Checkbox>
                  <Checkbox value="2">Female</Checkbox>
                </HStack>
              </CheckboxGroup>
            </FormControl>
          <FormControl mt={4} id="username">
            <FormLabel>Username</FormLabel>
            <Input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
            />
          </FormControl>
          <FormControl mt={4} id="password">
            <FormLabel>Password</FormLabel>
            <Input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
            />
          </FormControl>
          <Button mt={4} colorScheme="teal" type="submit">
            Register
          </Button>
          {error && <Box mt={4} color="red.500">{error}</Box>}
        </form>
      </Box>
    </Center>
  );
};

export default Register;
