// Navbar.js
import React, { useEffect, useState } from "react";
import {
  Box,
  Flex,
  Spacer,
  Link,
  Image,
  Text,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
} from "@chakra-ui/react";
import { SettingsIcon } from '@chakra-ui/icons'
import { useNavigate} from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState("");

  useEffect(() => { 
    const checkUsername = () => {
      const user = JSON.parse(localStorage.getItem('user'));
      if (user) {
        setUser(user);
      } else {
        setUser("");
      }
    }
    checkUsername();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('chatrooms');
    setUser(""); // Update the user state to reflect logout
    navigate("/"); // Navigate to the login page after logout
    window.location.reload();
  };
  

  return (
    <Box bg="#0066CC" w="100%" h="110px" display="flex" p={4} margin="auto">
      <Flex align="center">
        <Image src="/img/TPRICOn4333.png" alt="Logo" boxSize="110px" />
      </Flex>
      <Spacer />
      <Flex align="center" justify="center">
        <Spacer />
        
          <Flex gap={2} align="center" justify="center" color="white" mr={4}>

          {/* Merged Link */}
          <Menu>
            <Flex>
          <MenuButton as={Link} display="flex" alignItems="center" mr={4}>
            <Image
              src="/img/usericon.jpg"
              alt="User Logo"
              boxSize="40px"
              borderRadius="50%"
              mr={2}
            />
            <Text color="white" fontWeight="bold" mr={2}>
              {user || 'Guest'}
            </Text>
          </MenuButton>
          <MenuList>
            {user ? (
              <MenuItem onClick={handleLogout}>Logout</MenuItem>
            ) : (
              <MenuItem as={Link} href="/signin">Login</MenuItem>
            )}
          </MenuList>
          </Flex>
        </Menu>
        {/* End of Merged Link */}

            <Box>{user ? (<Link _hover={{ textDecoration: "none" }} onClick={handleLogout}> Logout </Link>):(<>   </>)}</Box>
          </Flex>
        <Link href="#" color="white" mr={4}>
          <SettingsIcon boxSize={5} />
        </Link>
      </Flex>
    </Box>
  );
};

export default Navbar;
